import React, { useState } from 'react';
import axios from '../services/api'; // Import Axios instance

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('/user/login', { email, password }); // POST request to backend
      const { token } = response.data;

      // Save the token in localStorage for session management
      localStorage.setItem('token', token);
      window.location.href = '/employees'; // Redirect to Employee List page
    } catch (err) {
      setError('Invalid credentials or server error.');
    }
  };

  return (
    <div>
      <h2>Login</h2>
      {error && <p>{error}</p>}
      <form onSubmit={handleLogin}>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default Login;
